import React, { useState, useEffect } from "react";
import axios from "axios";
import * as XLSX from "xlsx";
import { saveAs } from "file-saver";
import "./App.css";

function App() {
  const [matricula, setMatricula] = useState("");
  const [dataInicial, setDataInicial] = useState("");
  const [dataFinal, setDataFinal] = useState("");
  const [registros, setRegistros] = useState([]);
  const [novaMatricula, setNovaMatricula] = useState("");
  const [mensagem, setMensagem] = useState("");
  const [mostrarModalExportar, setMostrarModalExportar] = useState(false);
  const [mostrarCheckbox, setMostrarCheckbox] = useState(false);
  const [selecionados, setSelecionados] = useState([]);
  const [mostrarModalExcluir, setMostrarModalExcluir] = useState(false);

  useEffect(() => {
    const buscarRegistrosDoMesAtual = async () => {
      const hoje = new Date();
      const ano = hoje.getFullYear();
      const mes = String(hoje.getMonth() + 1).padStart(2, "0");
      const diaFinal = String(hoje.getDate()).padStart(2, "0");

      const dataInicial = `${ano}-${mes}-01`;
      const dataFinal = `${ano}-${mes}-${diaFinal}`;

      try {
        const response = await axios.get("https://SEU_BACKEND_URL/frequencia", {
          params: { dataInicial, dataFinal },
        });
        setRegistros(response.data);
      } catch (error) {
        console.error("Erro ao carregar registros:", error);
      }
    };

    buscarRegistrosDoMesAtual();
  }, []);

  const buscarFrequencia = async () => {
    try {
      const params = {};
      if (matricula) params.matricula = matricula;
      if (dataInicial) params.dataInicial = dataInicial;
      if (dataFinal) params.dataFinal = dataFinal;

      const response = await axios.get("https://SEU_BACKEND_URL/frequencia", {
        params,
      });
      setRegistros(response.data);
    } catch (error) {
      console.error("Erro ao buscar frequência:", error);
    }
  };

  const limparFiltros = async () => {
    setMatricula("");
    setDataInicial("");
    setDataFinal("");
    const response = await axios.get("https://SEU_BACKEND_URL/frequencia");
    setRegistros(response.data);
  };

  const registrarPresenca = async () => {
    if (!novaMatricula) return alert("Digite uma matrícula válida");

    try {
      await axios.post("https://SEU_BACKEND_URL/registrar", {
        matricula: novaMatricula,
      });
      setMensagem(`Presença registrada para ${novaMatricula}`);
      setNovaMatricula("");
      buscarFrequencia();

      setTimeout(() => {
        setMensagem("");
      }, 3000);
    } catch {
      alert("Erro ao registrar presença");
    }
  };

  const exportarParaExcel = async () => {
    try {
      const response = await axios.get("https://SEU_BACKEND_URL/frequencia", {
        params: {
          matricula: matricula || undefined,
          dataInicial: dataInicial || undefined,
          dataFinal: dataFinal || undefined,
        },
      });

      const dados = response.data;
      if (dados.length === 0) return alert("Nenhum dado encontrado.");

      const worksheet = XLSX.utils.json_to_sheet(
        dados.map((item) => ({
          ID: item.id,
          Matrícula: item.matricula,
          "Data/Hora": new Date(item.data).toLocaleString("pt-BR"),
        }))
      );
      const workbook = XLSX.utils.book_new();
      XLSX.utils.book_append_sheet(workbook, worksheet, "Frequência");

      const excelBuffer = XLSX.write(workbook, {
        bookType: "xlsx",
        type: "array",
      });

      const blob = new Blob([excelBuffer], {
        type: "application/octet-stream",
      });

      saveAs(blob, "frequencia.xlsx");
      setMostrarModalExportar(false);
    } catch (error) {
      alert("Erro ao exportar");
      console.error(error);
    }
  };

  const alternarCheckbox = () => {
    setMostrarCheckbox(!mostrarCheckbox);
    setSelecionados([]);
  };

  const handleSelecionar = (id) => {
    setSelecionados((prev) =>
      prev.includes(id) ? prev.filter((i) => i !== id) : [...prev, id]
    );
  };

  const deletarRegistros = async () => {
    try {
      await axios.post("https://SEU_BACKEND_URL/frequencia/delete", {
        ids: selecionados,
      });
      setMostrarModalExcluir(false);
      setSelecionados([]);
      setMostrarCheckbox(false);
      buscarFrequencia();
    } catch (error) {
      alert("Erro ao deletar registros");
      console.error(error);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <div className="max-w-4xl mx-auto bg-white p-6 rounded shadow">
        <h1 className="text-2xl font-bold text-center mb-6 text-gray-800">
          Sistema de Controle de Frequência
        </h1>

        <div className="mb-6">
          <h2 className="text-lg font-semibold mb-2">Registrar presença</h2>
          <div className="flex gap-4">
            <input
              type="text"
              placeholder="Matrícula"
              value={novaMatricula}
              onChange={(e) => setNovaMatricula(e.target.value)}
              className="border border-gray-300 px-4 py-2 rounded w-full"
            />
            <button
              onClick={registrarPresenca}
              className="bg-gray-800 text-white px-4 py-2 rounded hover:bg-gray-900"
            >
              Registrar
            </button>
          </div>
          {mensagem && (
            <p className="text-green-600 mt-2 transition-all duration-500 ease-in-out opacity-100">
              {mensagem}
            </p>
          )}
        </div>

        <div className="mb-6">
          <h2 className="text-lg font-semibold mb-2 text-gray-700">
            Filtrar presença
          </h2>
          <div className="grid grid-cols-1 sm:grid-cols-5 gap-4">
            <input
              type="text"
              placeholder="Matrícula"
              value={matricula}
              onChange={(e) => setMatricula(e.target.value)}
              className="border border-gray-300 rounded px-4 py-2 w-full"
            />
            <input
              type="date"
              value={dataInicial}
              onChange={(e) => setDataInicial(e.target.value)}
              className="border border-gray-300 rounded px-4 py-2 w-full"
            />
            <input
              type="date"
              value={dataFinal}
              onChange={(e) => setDataFinal(e.target.value)}
              className="border border-gray-300 rounded px-4 py-2 w-full"
            />
            <button
              onClick={buscarFrequencia}
              className="bg-gray-700 text-white px-4 py-2 rounded hover:bg-gray-800 w-full"
            >
              Buscar
            </button>
            <button
              onClick={limparFiltros}
              className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600 w-full"
            >
              Limpar
            </button>
          </div>
        </div>

        <div className="flex justify-between mb-4">
          <button
            onClick={alternarCheckbox}
            className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600"
          >
            {mostrarCheckbox ? "Cancelar" : "Deletar Registros"}
          </button>

          <button
            onClick={() => setMostrarModalExportar(true)}
            className="bg-gray-700 text-white px-4 py-2 rounded hover:bg-gray-800"
          >
            Exportar Excel
          </button>
        </div>

        <div className="overflow-x-auto mb-4">
          <table className="w-full table-auto border-collapse rounded-lg overflow-hidden">
            <thead>
              <tr className="bg-gray-200 text-gray-700">
                <th className="text-left px-4 py-3 border-b border-gray-300">
                  {mostrarCheckbox && "✓"}
                </th>
                <th className="text-left px-4 py-3 border-b border-gray-300">
                  ID
                </th>
                <th className="text-left px-4 py-3 border-b border-gray-300">
                  Matrícula
                </th>
                <th className="text-left px-4 py-3 border-b border-gray-300">
                  Data/Hora
                </th>
              </tr>
            </thead>
            <tbody>
              {registros.length === 0 ? (
                <tr>
                  <td colSpan="4" className="text-center py-6 text-gray-500">
                    Nenhum registro encontrado.
                  </td>
                </tr>
              ) : (
                registros.map((registro) => (
                  <tr
                    key={registro.id}
                    className="hover:bg-gray-100 transition-colors border-b border-gray-200"
                  >
                    <td className="px-4 py-3">
                      {mostrarCheckbox && (
                        <input
                          type="checkbox"
                          checked={selecionados.includes(registro.id)}
                          onChange={() => handleSelecionar(registro.id)}
                        />
                      )}
                    </td>
                    <td className="px-4 py-3">{registro.id}</td>
                    <td className="px-4 py-3">{registro.matricula}</td>
                    <td className="px-4 py-3">
                      {new Date(registro.data).toLocaleString("pt-BR")}
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>

        {mostrarCheckbox && selecionados.length > 0 && (
          <div className="flex justify-end mt-4">
            <button
              onClick={() => setMostrarModalExcluir(true)}
              className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
            >
              Deletar selecionados
            </button>
          </div>
        )}
      </div>

      {mostrarModalExportar && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded shadow w-full max-w-md">
            <h2 className="text-xl font-semibold mb-4 text-gray-800">
              Exportar para Excel
            </h2>
            <p>Use os filtros acima e clique em "Exportar agora"</p>
            <div className="flex justify-end gap-2 mt-4">
              <button
                onClick={() => setMostrarModalExportar(false)}
                className="bg-gray-400 text-white px-4 py-2 rounded"
              >
                Cancelar
              </button>
              <button
                onClick={exportarParaExcel}
                className="bg-gray-700 text-white px-4 py-2 rounded hover:bg-gray-800"
              >
                Exportar agora
              </button>
            </div>
          </div>
        </div>
      )}

      {mostrarModalExcluir && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded shadow w-full max-w-md">
            <h2 className="text-xl font-semibold mb-4 text-red-700">
              Confirmar exclusão
            </h2>
            <p>Deseja realmente excluir os registros selecionados?</p>
            <div className="flex justify-end gap-2 mt-4">
              <button
                onClick={() => setMostrarModalExcluir(false)}
                className="bg-gray-400 text-white px-4 py-2 rounded"
              >
                Cancelar
              </button>
              <button
                onClick={deletarRegistros}
                className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
              >
                Deletar agora
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
